import React, { useEffect, useState } from "react";
import * as RBS from "react-bootstrap";
import { useParams } from "react-router-dom";
import { connect } from "react-redux";
import { getMainRequest } from "../../redux";
import Subbar from "../Subbar";

// material import
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

function MainHome({ getMainRequest, contentList, userId }) {
  let voteHits = [];
  let voteID = [];
  let voteRegDate = [];
  let voteTitle = [];

  useEffect(() => {
    getMainRequest();
  }, []);

  //데이터 받기
  const contentItems = contentList.map((content) => (
    <div key={content.voteID} style={{ visibility: "hidden" }}>
      {
        (voteHits.push(content.voteHits),
        voteID.push(content.voteID),
        voteRegDate.push(content.voteRegDate),
        voteTitle.push(content.voteTitle))
      }
    </div>
  ));

  //Contents에 관한 부분
  function mainDisplay() {
    let P = [];
    // for (let i = 0; i < items.selecContent.length; i++) {
    //   P.push((items.selecHits[i] / items.voteHits) * 100);
    //   P[i] = P[i].toFixed(2);
    // }

    let voteHits = [];
    let voteID = [];
    let voteRegDate = [];
    let voteTitle = [];

    let tmp = [1, 2, 3, 4, 5, 6, 7, 8];
    return (
      <div className={useStyles.root}>
        <Subbar></Subbar>
        <Grid container spacing={2}>
          {contentList.map((content, index) => (
            <Grid item xs={6} sm={3}>
              <Link key={index} to={`/content/${tmp[index]}`}>
                <MediaCard
                  voteHits={content.voteHits}
                  voteID={content.voteID}
                  voteRegDate={voteRegDate}
                  voteTitle={voteTitle}
                ></MediaCard>
              </Link>
            </Grid>
          ))}
        </Grid>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    userId: state.authentication.status.currentUser,
    contentList: state.contents,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getMainRequest: () => {
      return dispatch(getMainRequest());
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(MainHome);
