import React, { Component, Fragment } from "react";
// import { connect } from "react-redux";
import propTypes from "prop-types";
import TextField from "@material-ui/core/TextField";
import { Button } from "@material-ui/core";
import "./createvote.css";

// 이미지 전송
import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";

// import axios from 'axios';

// import { CreateVotes } from '../redux/makevote/actions';

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

class CreateVote extends Component {
  state = {
    userId: "",
    voteTitle: "",
    voteContents: [""],
    picture: {},
    // 이미지
  };

  handleChange = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  };

  addAnswer = (e) => {
    this.setState({ voteContents: [...this.state.voteContents, ""] });
  };

  removeAnswer = (index) => {
    const newContents = this.state.voteContents.filter((content, i) => {
      return i !== index;
    });

    this.setState({ voteContents: newContents });
  };

  handleAnswer = (e, index) => {
    const voteContents = [...this.state.voteContents];
    voteContents[index] = e.target.value;
    this.setState({ voteContents });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    console.log(this.state);
    let body = {
      voteId: this.state.voteId,
      voteTitle: this.state.voteTitle,
      voteContent: this.state.voteContents,
    };

    console.log(body);

    this.props.onPost(body).then(() => {
      // 보내지면 빈칸으로 변경
      this.setState({
        userId: "",
        voteTitle: "",
        voteContents: [""],
      });
    });
    // this.props.CreateVotes(this.state);
  };

  // 이미지 전송
  uploadPicture = (e) => {
    this.setState({
      /* contains the preview, if you want to show the picture to the user
          you can access it with this.state.currentPicture
       */
      picturePreview: URL.createObjectURL(e.target.files[0]),
      /* this contains the file we want to send */
      pictureAsFile: e.target.files[0],
    });
  };

  setImageAction = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("file", this.state.picture.pictureAsFile);

    console.log(this.state.picture.pictureAsFile);

    for (var key of formData.entries()) {
      console.log(key[0] + ", " + key[1]);
    }

    const data = await fetch("http://localhost:3000/upload/post", {
      method: "post",
      headers: { "Content-Type": "multipart/form-data" },
      body: formData,
    });
    const uploadedImage = await data.json();
    if (uploadedImage) {
      console.log("Successfully uploaded image");
    } else {
      console.log("Error Found");
    }
  };

  render() {
    const voteContents = this.state.voteContents.map((content, i) => (
      <Fragment key={i}>
        <label className="form-label"></label>
        <TextField
          id="outlined-basic"
          label="항목"
          variant="outlined"
          className="content"
          type="text"
          value={content}
          key={i}
          onChange={(e) => this.handleAnswer(e, i)}
        />
        <div className="button-container">
          <Button
            className="remove-button"
            type="button"
            variant="contained"
            color="secondary"
            size="medium"
            onClick={(e) => this.removeAnswer(i)}
          >
            삭제
          </Button>
        </div>
      </Fragment>
    ));

    return (
      <form className="form" onSubmit={this.handleSubmit}>
        <label className="form-label" htmlFor="voteTitle">
          투표 제목
        </label>
        <input
          className="form-input"
          type="text"
          name="voteTitle"
          value={this.state.voteTitle}
          onChange={this.handleChange}
        />
        <div className="conContainer">{voteContents}</div>
        <div className="buttons_center">
          <Button
            className="button"
            type="button"
            variant="contained"
            color="primary"
            size="medium"
            onClick={this.addAnswer}
          >
            항목 추가
          </Button>
          <Button className="button" type="submit" onClick={this.handleSubmit}>
            투표 만들기
          </Button>
        </div>
        <div>
          <form onSubmit={this.setImageAction}>
            <input type="file" name="image" onChange={this.uploadPicture} />
            <br />
            <br />
            <button type="submit" name="upload">
              Upload
            </button>
          </form>
        </div>
      </form>
    );
  }
}

CreateVote.propTypes = {
  onPost: propTypes.func,
};

CreateVote.defaultProps = {
  onPost: (body) => {
    console.error("post function not defined");
  },
};

export default CreateVote;
// export default connect(() => ({}), { CreateVotes })(CreateVote);
