import React, { Component, Fragment } from "react";
// import { connect } from "react-redux";
import propTypes from "prop-types";
import TextField from "@material-ui/core/TextField";
import { Button } from "@material-ui/core";
import "./createvote.css";

// 이미지 전송
import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import Dropzone from "react-dropzone";
import MyDropzone from "./MyDropzone";
// import axios from 'axios';

// import { CreateVotes } from '../redux/makevote/actions';

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

const onDrop = useCallback(async (acceptedFiles) => {
  // 사용자가 올린 정보를 확인해야 하므로 일단 서버로 전송합니다.
  // 제목 같은 건 폼을 제출한 이후에 달아주도록 합시다.

  // 폼데이터 구성
  const formData = new FormData();
  const config = {
    header: {
      "content-type": "multipart/form-data",
    },
  };
  formData.append("file", acceptedFiles[0]);
  console.log(acceptedFiles[0]);

  // 배포시에는 지워줘야 합니다.
  axios.defaults.baseURL = "http://localhost:5000/";
  await axios.post("/api/image/upload", formData, config).then((res) => {
    console.log(res);
  });
}, []);

const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

const InputProps = {
  ...getInputProps(),
  multiple: false,
  accept: "image/gif, image/jpg, image/jpeg",
};

const RootProps = {
  ...getRootProps(),
};

class CreateVote extends Component {
  state = {
    userId: "",
    voteTitle: "",
    voteContents: [""],
    file: [],
    // 이미지
  };

  handleChange = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  };

  addAnswer = (e) => {
    this.setState({ voteContents: [...this.state.voteContents, ""] });
  };

  removeAnswer = (index) => {
    const newContents = this.state.voteContents.filter((content, i) => {
      return i !== index;
    });

    this.setState({ voteContents: newContents });
  };

  handleAnswer = (e, index) => {
    const voteContents = [...this.state.voteContents];
    voteContents[index] = e.target.value;
    this.setState({ voteContents });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    console.log(this.state);
    let body = {
      voteId: this.state.voteId,
      voteTitle: this.state.voteTitle,
      voteContent: this.state.voteContents,
    };

    console.log(body);

    this.props.onPost(body).then(() => {
      // 보내지면 빈칸으로 변경
      this.setState({
        userId: "",
        voteTitle: "",
        voteContents: [""],
      });
    });
    // this.props.CreateVotes(this.state);
  };

  // 이미지 전송

  submitImg = (e) => {
    e.preventDefault();

    console.log(FileReader);
  };

  render() {
    const voteContents = this.state.voteContents.map((content, i) => (
      <Fragment key={i}>
        <label className="form-label"></label>
        <TextField
          id="outlined-basic"
          label="항목"
          variant="outlined"
          className="content"
          type="text"
          value={content}
          key={i}
          onChange={(e) => this.handleAnswer(e, i)}
        />
        <div className="button-container">
          <Button
            className="remove-button"
            type="button"
            variant="contained"
            color="secondary"
            size="medium"
            onClick={(e) => this.removeAnswer(i)}
          >
            삭제
          </Button>
        </div>
      </Fragment>
    ));

    return (
      <form className="form" onSubmit={this.handleSubmit}>
        <label className="form-label" htmlFor="voteTitle">
          투표 제목
        </label>
        <input
          className="form-input"
          type="text"
          name="voteTitle"
          value={this.state.voteTitle}
          onChange={this.handleChange}
        />
        <div className="conContainer">{voteContents}</div>
        <div className="buttons_center">
          <Button
            className="button"
            type="button"
            variant="contained"
            color="primary"
            size="medium"
            onClick={this.addAnswer}
          >
            항목 추가
          </Button>
          <Button className="button" type="submit" onClick={this.handleSubmit}>
            투표 만들기
          </Button>
        </div>
        <div>
          <Dropzone {...RootProps} maxSize={100} multiple={false}>
            <input {...InputProps} />
            {isDragActive ? (
              <p>이제 이미지를 놓아주세요</p>
            ) : (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                <div style={{ fontSize: "3em", marginBottom: "5px" }}>
                  <i className="fas fa-file-upload"></i>
                </div>
                <div>이미지 드랍 or 클릭</div>
              </div>
            )}
          </Dropzone>
        </div>
      </form>
    );
  }
}

CreateVote.propTypes = {
  onPost: propTypes.func,
};

CreateVote.defaultProps = {
  onPost: (body) => {
    console.error("post function not defined");
  },
};

export default CreateVote;
// export default connect(() => ({}), { CreateVotes })(CreateVote);
