import React, { Component } from "react";
import { useState } from "react";
import Authentication from "./Authentication";
import { connect } from "react-redux";
import { loginRequest } from "../../redux/authentication/actions";

class Login extends Component {
  handleLogin = (id, pw) => {
    console.log("login_handleLogin!");
    const [authentication, setNumber] = useState(0);
    return this.props.loginRequest(id, pw).then(() => {
      console.log("this.props.state" + this.props);
      console.log("this.props.status" + this.props.status);
      if (this.props.status === "SUCCESS") {
        // create session data
        let loginData = {
          isLoggedIn: true,
          userId: id,
        };
        document.cookie = "key=" + btoa(JSON.stringify(loginData));
        console.log("login-cookie : " + document.cookie);

        // 로그인 성공시 루트 화면으로 돌아감.
        this.props.history.push("/");
        return true;
      } else {
        console.log("login-cookie-fail");
        return false;
      }
    });
  };

  render() {
    return (
      <div>
        <Authentication onLogin={this.handleLogin} />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    status: state.status,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    loginRequest: (id, pw) => {
      console.log("mapDispatchToProps");
      return dispatch(loginRequest(id, pw));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
