import React, { Component, Fragment } from "react";
// import { connect } from "react-redux";
import propTypes from "prop-types";
import TextField from "@material-ui/core/TextField";
import { Button } from "@material-ui/core";
import "./createvote.css";

// 이미지 전송
import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";

// import axios from 'axios';

// import { CreateVotes } from '../redux/makevote/actions';

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

class CreateVote extends Component {
  state = {
    userId: "",
    voteTitle: "",
    voteContents: [""],
    // 이미지
    img: null,
  };

  handleChange = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  };

  addAnswer = (e) => {
    this.setState({ voteContents: [...this.state.voteContents, ""] });
  };

  removeAnswer = (index) => {
    const newContents = this.state.voteContents.filter((content, i) => {
      return i !== index;
    });

    this.setState({ voteContents: newContents });
  };

  handleAnswer = (e, index) => {
    const voteContents = [...this.state.voteContents];
    voteContents[index] = e.target.value;
    this.setState({ voteContents });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    console.log(this.state);
    let body = {
      voteId: this.state.voteId,
      voteTitle: this.state.voteTitle,
      voteContent: this.state.voteContents,
    };

    console.log(body);

    this.props.onPost(body).then(() => {
      // 보내지면 빈칸으로 변경
      this.setState({
        userId: "",
        voteTitle: "",
        voteContents: [""],
      });
    });
    // this.props.CreateVotes(this.state);
  };

  // 이미지 전송

  onChangeImg = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.files[0];
    this.setState(nextState);
  };

  isSelectedImg = (event: React.ChangeEvent<HTMLInputElement>) => {
    //빈파일이 아닌 경우 함수 진행
    if (event.target.files !== null) {
      //FormData 생성
      const fd = new FormData();
      //FormData에 key, value 추가하기
      fd.append("filename", event.target.files[0]);
      // axios 사용해서 백엔드에게 파일 보내기
      axios
        .post(`${URL}/user/profile-upload`, fd)
        .then((res) => {
          //응답으로 받은 url 저장하기 (응답 내용의 표시나 방법은 백엔드와 결정해야한다.)
          setImgUpload(res.data.image_url);
          // 최종적으로 DB에 저장될 url을 보내는 과정이 부모 컴포넌트에 있기 때문에 부모 컴포넌트에 url을 보내는 과정
          props.imgStore(res.data.image_url);
        })
        //에러가 날 경우 처리
        .catch((error) => {
          console.log(error.response);
        });
    }
  };

  render() {
    const voteContents = this.state.voteContents.map((content, i) => (
      <Fragment key={i}>
        <label className="form-label"></label>
        <TextField
          id="outlined-basic"
          label="항목"
          variant="outlined"
          className="content"
          type="text"
          value={content}
          key={i}
          onChange={(e) => this.handleAnswer(e, i)}
        />
        <div className="button-container">
          <Button
            className="remove-button"
            type="button"
            variant="contained"
            color="secondary"
            size="medium"
            onClick={(e) => this.removeAnswer(i)}
          >
            삭제
          </Button>
        </div>
      </Fragment>
    ));

    return (
      <form className="form" onSubmit={this.handleSubmit}>
        <label className="form-label" htmlFor="voteTitle">
          투표 제목
        </label>
        <input
          className="form-input"
          type="text"
          name="voteTitle"
          value={this.state.voteTitle}
          onChange={this.handleChange}
        />
        <div className="conContainer">{voteContents}</div>
        <div className="buttons_center">
          <Button
            className="button"
            type="button"
            variant="contained"
            color="primary"
            size="medium"
            onClick={this.addAnswer}
          >
            항목 추가
          </Button>
          <Button className="button" type="submit" onClick={this.handleSubmit}>
            투표 만들기
          </Button>
        </div>
        <div>
          <input
            accept="image/*"
            className={useStyles.input}
            id="contained-button-file"
            multiple
            type="file"
            onChange={this.isSelectedImg}
          />
          <label htmlFor="contained-button-file">
            <Button variant="contained" color="primary" component="span">
              Upload
            </Button>
          </label>
        </div>
      </form>
    );
  }
}

CreateVote.propTypes = {
  onPost: propTypes.func,
};

CreateVote.defaultProps = {
  onPost: (body) => {
    console.error("post function not defined");
  },
};

export default CreateVote;
// export default connect(() => ({}), { CreateVotes })(CreateVote);
