import React, { useEffect } from "react";
import * as RBS from "react-bootstrap";
import { useParams } from "react-router-dom";

import { connect } from "react-redux";
import { getVotes } from "../redux";
import RegisterComment from "./RegisterComment";

import testImage from "../img/content_img.png";

function VotePage({ getVotes, loading, items }) {
  useEffect(() => {
    getVotes();
  }, []);
  console.log(items);

  const voteItems = loading ? (
    <div>is loading...</div>
  ) : (
    items.map((vote) => (
      <div key={vote.id}>
        <h3>{vote.name}</h3>
        <p>{vote.email}</p>
        <p>{vote.body}</p>
      </div>
    ))
  );

  class Test {
    constructor(name, date, voter, content) {
      this.name = name;
      this.date = date;
      this.voter = voter;
      this.content = content;
    }
  }
  let test = new Test("제목", "2021.05.11", 1500, [
    "추억과 김밥",
    "밀알 식당",
    "논두렁 갈비",
  ]);

  const { nam } = useParams();

  function VoteTop() {
    return (
      <div className="VotePage">
        <h1>
          {test.name}
          {nam}
        </h1>
        <h5>
          {test.date} | {test.voter}명
        </h5>
        <RBS.Col xs={6} md={4}>
          {/* ../public/content_img.png 이라고 해도 되지만 위치몰라도 고를 수 있는걸 보여주고 싶었음*/}
          <RBS.Image src={testImage} rounded />
        </RBS.Col>
      </div>
    );
  }

  function contentList() {
    return (
      <ul className="list_content">
        {test.content.map((item, index) => (
          <tr key={index}>
            <td>
              <input type="checkbox" />
            </td>
            <td>
              <div>
                Content{index + 1} {item}
              </div>
            </td>
          </tr>
        ))}
      </ul>
    );
  }

  // 댓글 등록
  function handleRegister(comments) {
    return this.props.loginRequest(comments).then(() => {
      console.log(this.props.history);
      if (this.props.status === "SUCCESS") {
        // create session data
        let loginData = {
          isLoggedIn: true,
          userId: id,
        };
        document.cookie = "key=" + btoa(JSON.stringify(loginData));
        console.log("login-cookie : ");
        console.log(document.cookie);

        // 로그인 성공시 루트 화면으로 돌아감.
        this.props.history.push("/");
        return true;
      } else {
        console.log("login-cookie-fail");
        return false;
      }
    });
  }
  return (
    <div className="VotePage">
      <RBS.Container>
        <RBS.Row>
          <RBS.Col>
            <VoteTop></VoteTop>
          </RBS.Col>
          <RBS.Col>
            {/* <div style="position: absolute; right: 0px; bottom: 0px;">
                          안녕하세요!!!
                        </div> */}
          </RBS.Col>
        </RBS.Row>
      </RBS.Container>
      {contentList()}
      <div>
        <RegisterComment register={this.handleLogin} />
      </div>
      <div class="votes">{voteItems}</div>
    </div>
  );
}
const mapStateToProps = (state) => {
  console.log(state);
  return {
    items: state.votes.items,
  };
};
const mapDispatchToProps = {
  getVotes,
};
export default connect(mapStateToProps, mapDispatchToProps)(VotePage);
