import React, { useEffect, useState } from "react";
import * as RBS from "react-bootstrap";
import { useParams } from "react-router-dom";

import { connect } from "react-redux";
import { getVotes, registerCommentsRequest } from "../redux";
import RegisterComment from "./RegisterComment";

import testImage from "../img/content_img.png";

// material import
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

function VotePage({ getVotes, loading, items }) {
  const [inputs, setInputs] = useState({
    name: "",
    nickname: "",
  });

  const { name, nickname } = inputs; // 비구조화 할당을 통해 값 추출

  const onChange = (e) => {
    const { value, name } = e.target; // 우선 e.target 에서 name 과 value 를 추출
    setInputs({
      ...inputs, // 기존의 input 객체를 복사한 뒤
      [name]: value, // name 키를 가진 값을 value 로 설정
    });
  };

  useEffect(() => {
    getVotes();
  }, []);
  console.log(items);

  const voteItems = loading ? (
    <div>is loading...</div>
  ) : (
    items.map((vote) => (
      <div key={vote.id}>
        <h3>{vote.name}</h3>
        <p>{vote.email}</p>
        <p>{vote.body}</p>
      </div>
    ))
  );

  class Test {
    constructor(name, date, voter, content) {
      this.name = name;
      this.date = date;
      this.voter = voter;
      this.content = content;
    }
  }
  let test = new Test("제목", "2021.05.11", 1500, [
    "추억과 김밥",
    "밀알 식당",
    "논두렁 갈비",
  ]);

  const { nam } = useParams();

  function VoteTop() {
    return (
      <div className="VotePage">
        <h1>
          {test.name}
          {nam}
        </h1>
        <h5>
          {test.date} | {test.voter}명
        </h5>
        <RBS.Col xs={6} md={4}>
          {/* ../public/content_img.png 이라고 해도 되지만 위치몰라도 고를 수 있는걸 보여주고 싶었음*/}
          <RBS.Image src={testImage} rounded />
        </RBS.Col>
      </div>
    );
  }

  function contentList() {
    return (
      <ul className="list_content">
        {test.content.map((item, index) => (
          <tr key={index}>
            <td>
              <input type="checkbox" />
            </td>
            <td>
              <div>
                Content{index + 1} {item}
              </div>
            </td>
          </tr>
        ))}
      </ul>
    );
  }

  // 댓글 등록
  function handleRegister(comments) {
    let body = {
      userId: this.props.userId,
      contentTitle: this.props.contentTitle,
      comments: comments,
    };
    return this.props.registerCommentsRequest(body).then(() => {
      console.log(this.props.history);
      if (this.props.status === "SUCCESS") {
        // 댓글 등록 성공 시 현재 화면으로 돌아감.
        //this.props.history.push("/");
        return true;
      }
    });
  }
  return (
    <div className="VotePage">
      <RBS.Container>
        <RBS.Row>
          <RBS.Col>
            <VoteTop></VoteTop>
          </RBS.Col>
          <RBS.Col>
            {/* <div style="position: absolute; right: 0px; bottom: 0px;">
                          안녕하세요!!!
                        </div> */}
          </RBS.Col>
        </RBS.Row>
      </RBS.Container>
      {contentList()}
      <div>
        <form className={this.useStyles.root}>
          <TextField
            id="standard-full-width"
            label="userId"
            style={{ margin: 8 }}
            placeholder="댓글을 입력하세요."
            fullWidth
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
            value={this.state.userId}
            onChange={this.handleChange}
          />
          <Button
            variant="contained"
            color="primary"
            className={this.useStyles.submit}
            onClick={this.handleRegister}
          >
            등록하기
          </Button>
        </form>
      </div>
      <div class="votes">{voteItems}</div>
    </div>
  );
}
const mapStateToProps = (state) => {
  console.log(state);
  return {
    items: state.votes.items,
  };
};
const mapDispatchToProps = {
  getVotes,
};
export default connect(mapStateToProps, mapDispatchToProps)(VotePage);
