// material import
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

class RegisterComment extends Component {
  state = {
    userId: "",
    userPass: "",
  };

  handleChange = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  };

  handleLogin = () => {
    let id = this.state.userId;
    let pw = this.state.userPass;

    this.props.onLogin(id, pw).then((success) => {
      // 실패하면 비밀번호 재입력 받음
      console.log("Auth_handleLogin");
      if (!success) {
        console.log(this.props);
        console.log("Auth_handleLogin_fail");
        this.setState({
          userPass: "",
        });
      } else {
      }
    });
  };

  render() {
    return (
      <div>
        <form className={classes.root}>
          <TextField
            id="standard-full-width"
            label="userId"
            style={{ margin: 8 }}
            placeholder="댓글을 입력하세요."
            fullWidth
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <Button
            variant="contained"
            color="primary"
            className={useStyles.submit}
            onClick={this.handleLogin}
          >
            등록하기
          </Button>
        </form>
        ;
      </div>
    );
  }
}

Authentication.propTypes = {
  onLogin: PropTypes.func,
};

Authentication.defaultProps = {
  onLogin: (id, pw) => {
    console.error("login function not defined");
  },
};

export default RegisterComment;
